import logo from './images/logo-black.png'
import searchImage from './images/search.png'
import './App.css';
import React, { useEffect, useState } from "react"
function DisplayData(prop){
  return(
  <div className='data'>
    <span className='name'>{prop.name}</span>
    <span className='region'>Province: {prop.region}</span>
    <span className='country'>Country: {prop.country}</span>
    <span className='condition'>Condition: {prop.condition} </span>
    <span className='feelsLike'>FeelsLike: {prop.feelsLike} °C </span>
    <span className='windSpeed'>WindSpeed: {prop.windSpeed} kph </span>
    <span className='pressure'>Pressure: {prop.pressure} mb </span>
    <span className='feelsLike'>Condition: {prop.feelsLike} °C </span>
  </div>
  )

}
function WeatherData(prop){
  const [weatherData,setWeatherData] = useState({})
  useEffect(()=>{
    console.log(prop.location)
    const options = {
      method : 'GET',
      headers: {
        'X-RapidAPI-Key': 'f9448be2b2mshd890e0a227ca40ap1e7d17jsn28b44a965b4a',
        'X-RapidAPI-Host': 'weatherapi-com.p.rapidapi.com'
      }
    }

    const url = `https://weatherapi-com.p.rapidapi.com/current.json?q=${prop.location}%2C-0.13`
    fetch(url,options).then(res =>res.json()).then(data =>setWeatherData(data))
    console.log(weatherData)
  },[prop.location])
  return(
    <div className='dataAll'>
    {Object.keys(weatherData).length > 0 ? (
      <DisplayData name = {weatherData.location.name} region={weatherData.location.region} country={weatherData.location.country} condition = {weatherData.current.condition.text} feelsLike =  {weatherData.current.feelslike_c} windSpeed = {weatherData.current.wind_kph} pressure = {weatherData.current.pressure_mb}/> 
    ): <p>An unexpected error has occured</p>}
  </div>

  )
}
function Navbar(){

  const [locationName,setLocationName] = useState("Lahore")
  const [formData,setFormData] = useState({
    Location:""
  })
  function handleChange(event){
    const {name,value} = event.target
    setFormData(prevFormData =>{
      return(
        {
          ...prevFormData,
          [name] : value
        }
      )
    })

  }
  function handleSubmit(event){
    event.preventDefault()
    setLocationName(formData.Location)
  }
  return(
    <div className='Main'>
      <nav>
        <div className='nav-left'>
          <span>Weather Data</span>
          <img src = {logo}></img>
        </div>
        <div className='nav-input'>
          <input type="text" placeholder = "Enter Location" name = "Location" value = {formData.Location} onChange = {handleChange}/>
          <button type='submit' onClick={handleSubmit}><img src ={searchImage}></img></button>
        </div>
        <div className='nav-right'>
          <span>Sign Up</span>
          <span>Log In</span>
          <span className='Rate'>Rate</span>
        </div>
      </nav>
      <WeatherData 
        location = {locationName}
      />
    </div>
  )
}

function App() {

  return (
    <div className="App">
      <Navbar/>
    </div>
  );
}

export default App;
