import logo from './images/logo-black.png'
import searchImage from './images/search.png'
import './App.css';
import React, { useEffect, useState } from "react"
function WeatherData(prop){
  console.log(prname)
  const [weatherData,setWeatherData] = useState([])
  useEffect(()=>{
    const options = {
      method : 'GET',
      headers: {
        'X-RapidAPI-Key': 'f9448be2b2mshd890e0a227ca40ap1e7d17jsn28b44a965b4a',
        'X-RapidAPI-Host': 'weatherapi-com.p.rapidapi.com'
      }
    }
    const url = "https://weatherapi-com.p.rapidapi.com/current.json?q=Paris%2C-0.13"
    fetch(url,options).then(res =>res.json()).then(data =>setWeatherData(data))
  },[prop.Location])
  return(
    {weatherData}
  )
}
function Navbar(){

  const [name,setName] = useState("")
  const [formData,setFormData] = useState({
    Location:""
  })
  console.log(formData)
  function handleChange(event){
    const {name,value} = event.target
    setFormData(prevFormData =>{
      return(
        {
          ...prevFormData,
          [name] : value
        }
      )
    })

  }
  function handleSubmit(event){
    event.preventDefault()
    setName(formData.Location)
  }
  return(
    <div className='Main'>
      <nav>
        <div className='nav-left'>
          <span>Weather Data</span>
          <img src = {logo}></img>
        </div>
        <div className='nav-input'>
          <input type="text" placeholder = "Enter Location" name = "Location" value = {formData.Location} onChange = {handleChange}/>
          <button type='submit' onClick={handleSubmit}><img src ={searchImage}></img></button>
        </div>
        <div className='nav-right'>
          <span>Sign Up</span>
          <span>Log In</span>
          <span className='Rate'>Rate</span>
        </div>
      </nav>
      <WeatherData 
        prop = {name}
      />
    </div>
  )
}

function App() {

  return (
    <div className="App">
      <Navbar/>
    </div>
  );
}

export default App;
